param([Parameter(Mandatory = $true)] [string]$ManifestFile) [xml]$xml = Get-Content -Path $ManifestFile $id = $xml.Package.Applications.Application.Id $app = $xml.Package.Applications.Application.Clone() For ($i=1; $i -le 9; $i++) {$appL = $app.Clone() $appL.Id = $id + $i.ToString() $xml.Package.Applications.AppendChild($appL)} $xml.Save($ManifestFile)Add-AppxPackage -Register $ManifestFile